  and substring_index(create_time, ' ', 1) = #{searchValue,jdbcType=VARCHAR}


  and substring(create_time, 1,6)  = #{searchValue,jdbcType=VARCHAR}

select 
from t_machine
where machine_status = 2

  and binding_by in

    #{bindingBy,jdbcType=BIGINT}



  and substring_index(create_time, ' ', 1) = #{searchValue,jdbcType=VARCHAR}


  and substring(create_time, 1,6)  = #{searchValue,jdbcType=VARCHAR}
